mapper.select:

select g_no, o_no, depth 
from board 
where no = #{no}

mapper.update:

update board 
set o_no = o_no + 1 
where g_no = #{groupNo} 
and o_no >= #{orderNo}

mapper.select:

select contents 
from board 
where no = #{no}


select title 
from board 
where no = #{no}


select user_no 
from board 
where no = #{no}


select count(*) 
from board 
where title like #{title}

mapper.update:

update board 
set hit = hit+1 
where no = #{no}

choose.when:

update board 
set contents = #{contents}
where no = #{no}


update board 
set title = #{title}
where no = #{no}

choose.otherwise:

update board 
set title = #{title}, contents = #{contents} 
where no = #{no}
